import React from 'react';

function OpportunityModal({ opportunities }) {
  // Extract unique doctors and patients from opportunities
  const doctors = Array.from(new Set(opportunities.map(op => `${op.doctor.first_name} ${op.doctor.last_name}`)));
  const patients = Array.from(new Set(opportunities.map(op => `${op.patient.first_name} ${op.patient.last_name}`)));

  return (
    <div className="modal fade" id="opportunitymodal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Add Opportunity</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <form className="member-form">
            <div className="modal-body pt-4">
              {/* Procedure Name Field */}
              <div className="form-group mb-3">
                <label htmlFor="procedureName">Procedure Name</label>
                <input type="text" className="form-control" id="procedureName" placeholder="Enter procedure name" />
              </div>
              {/* Doctor's Name Select Field */}
              <div className="form-group mb-3">
                <label htmlFor="doctorName">Doctor's Name</label>
                <select className="form-select" id="doctorName">
                  <option value="">Select a Doctor</option>
                  {doctors.map((doc, index) => (
                    <option key={index} value={doc}>{doc}</option>
                  ))}
                </select>
              </div>
              {/* Patient's Name Select Field */}
              <div className="form-group mb-3">
                <label htmlFor="patientName">Patient's Name</label>
                <select className="form-select" id="patientName">
                  <option value="">Select a Patient</option>
                  {patients.map((pat, index) => (
                    <option key={index} value={pat}>{pat}</option>
                  ))}
                </select>
              </div>
            </div>
            <div className="modal-footer border-0 pt-0">
              <button type="submit" className="btn btn-primary">Save</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default OpportunityModal;
