import React, { useState } from 'react';
import { createMember } from '../services/api'; // Import the createMember function
import { useDataDispatch } from '../contexts/DataContext'; // Import the data dispatch hook

const CreateMemberForm = () => {
  const dispatch = useDataDispatch(); // Access the dispatch function from the context
  const [memberData, setMemberData] = useState({
    name: '',
    email: '',
    image: null, // Initialize image as null
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMemberData({
      ...memberData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setMemberData({
      ...memberData,
      image: e.target.files[0], // Update image with the selected file
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('member[name]', memberData.name);
        formData.append('member[email]', memberData.email);
        formData.append('member[avatar]', memberData.image);

      const response = await createMember(formData); // Use the createMember function from the service

      if (response.ok) {
        // Handle success
        console.log('Member created successfully');
        // Dispatch an action to update the context state with the created member data
        dispatch({ type: 'CREATE_MEMBER', payload: response.data });
      } else {
        // Handle error
        const errorData = await response.json();
        console.error('Error creating member:', errorData);
      }
    } catch (error) {
      console.error('Error creating member:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={memberData.name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={memberData.email}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label htmlFor="image">Image:</label>
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
      </div>
      <button type="submit">Create Member</button>
    </form>
  );
};

export default CreateMemberForm;
