import React, { useState } from 'react';
import axios from 'axios';

function OpportunityModal({ opportunities }) {
  const [procedureName, setProcedureName] = useState('');
  const [doctorId, setDoctorId] = useState('');
  const [patientId, setPatientId] = useState('');

  // Unique doctors and patients from opportunities
  const doctors = Array.from(new Map(opportunities.map(op => [op.doctor.id, { id: op.doctor.id, name: `${op.doctor.first_name} ${op.doctor.last_name}` }])).values());

  // Creating a unique list of patients
  const patients = Array.from(new Map(opportunities.map(op => [op.patient.id, { id: op.patient.id, name: `${op.patient.first_name} ${op.patient.last_name}` }])).values());

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:3000/api/v1/opportunities', {
        procedure_name: procedureName,
        doctor_id: doctorId,
        patient_id: patientId
      });
      console.log(response.data); // Handle the response as needed
    } catch (error) {
      console.error('Error making the API request:', error);
    }
  };

  return (
    <div className="modal fade" id="opportunitymodal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Add Opportunity</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <form className="member-form" onSubmit={handleSubmit}>
            <div className="modal-body pt-4">
              {/* Procedure Name Field */}
              <div className="form-group mb-3">
                <label htmlFor="procedureName">Procedure Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="procedureName"
                  placeholder="Enter procedure name"
                  value={procedureName}
                  onChange={(e) => setProcedureName(e.target.value)}
                />
              </div>
              {/* Doctor's Name Select Field */}
              <div className="form-group mb-3">
                <label htmlFor="doctorName">Doctor's Name</label>
                <select
                  className="form-select"
                  id="doctorName"
                  value={doctorId}
                  onChange={(e) => setDoctorId(e.target.value)}
                >
                  <option value="">Select a Doctor</option>
                  {doctors.map((doc) => (
                    <option key={doc.id} value={doc.id}>{doc.name}</option>
                  ))}
                </select>
              </div>
              {/* Patient's Name Select Field */}
              <div className="form-group mb-3">
                <label htmlFor="patientName">Patient's Name</label>
                <select
                  className="form-select"
                  id="patientName"
                  value={patientId}
                  onChange={(e) => setPatientId(e.target.value)}
                >
                  <option value="">Select a Patient</option>
                  {patients.map((pat) => (
                    <option key={pat.id} value={pat.id}>{pat.name}</option>
                  ))}
                </select>
              </div>
            </div>
            <div className="modal-footer border-0 pt-0">
              <button type="submit" className="btn btn-primary">Save</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default OpportunityModal;
