import React, { useState, useEffect } from 'react';
import { createMember } from '../services/api'; // Import the createMember function
import { useDataDispatch } from '../contexts/DataContext'; // Import the data dispatch hook

const CreateMemberForm = () => {
  const dispatch = useDataDispatch(); // Access the dispatch function from the context
  const [memberData, setMemberData] = useState({
    name: '',
    email: '',
    image: null, // Initialize image as null
  });

  const [createdMember, setCreatedMember] = useState(null);


  useEffect(() => {
    // Function to fetch the member's avatar image
    const fetchMemberAvatar = async () => {
      if (createdMember) {
        try {
          // Replace `:id` with the actual member's ID
          const response = await fetch(`/api/members/${createdMember.id}/avatar`);
          if (response.status === 200) {
            // Assuming the response contains the image URL
            const imageResponse = await response.json();
            setCreatedMember({
              ...createdMember,
              avatar: imageResponse.url,
            });
          } else {
            console.error('Error fetching member avatar:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching member avatar:', error);
        }
      }
    };

    fetchMemberAvatar();
  }, [createdMember]);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMemberData({
      ...memberData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setMemberData({
      ...memberData,
      image: e.target.files[0], // Update image with the selected file
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('member[name]', memberData.name);
        formData.append('member[email]', memberData.email);
        formData.append('member[avatar]', memberData.image);

      const response = await createMember(formData); // Use the createMember function from the service
      debugger
      if (response.status == "201") {
        // Handle success

        console.log('Member created successfully');
        // Dispatch an action to update the context state with the created member data
        dispatch({ type: 'CREATE_MEMBER', payload: response.data });
        setCreatedMember(response.data);
      } else {
        // Handle error
        // const errorData = await response.json();
        // console.error('Error creating member:', errorData);
      }
    } catch (error) {
      console.error('Error creating member:', error);
    }
  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={memberData.name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={memberData.email}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label htmlFor="image">Image:</label>
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
      </div>
      <button type="submit">Create Member</button>
    </form>
       {createdMember && (
        <div>
          <h3>Created Member:</h3>
          <p>ID: {createdMember.id}</p>
          <p>Name: {`${createdMember.first_name} ${createdMember.last_name}`}</p>
          <p>Email: {createdMember.email}</p>
          {createdMember.avatar && (
            <div>
              <h4>Avatar:</h4>
              <img src={createdMember.avatar.url} alt="Avatar" />
            </div>
          )}
        </div>
      )}
      </>
  );
};

export default CreateMemberForm;
